    map.getView().setZoom(zoom);
  }
});
appsmith.onReady(() => {
  const {
    lat,
    lng,
    zoom
  } = appsmith.model;
  const coord = [lng, lat];
  // Set up the OSM layer
  var myTileServer = new ol.layer.Tile({
    source: new ol.source.OSM({
      crossOrigin: null,
      url: 'https://{a-c}.tile.openstreetmap.org/{z}/{x}/{y}.png'
    })
  });
  // Create the map
  map = new ol.Map({
